package Lesson4;

public class Sample5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int a = 0;
		int b = 0;
		
		a = a++;
		b = ++b;
		
		System.out.println("代入後にインクリメントしたのでｂの値は"+ a + "です。");
		System.out.println("代入前にインクリメントしたのでｂの値は"+ b + "です。");
	}

}
